codeComQuery:
-- 1 See all students
SELECT * FROM students;

-- 2 Top 3 students
SELECT name, marks FROM students ORDER BY marks DESC LIMIT 3;

-- 3 Bottom 2 students
SELECT name, marks FROM students ORDER BY marks ASC LIMIT 2;

-- 4 Skip first 2 and show next 3
SELECT name, marks FROM students ORDER BY marks DESC LIMIT 3 OFFSET 2;
